{						tk = n;					}					return tk;				}								function fnListSort(selection)				{					var selSort = document.getElementById("selectSort").value;					var selCond = document.getElementById("selectCondition").value;					var selBrnd = document.getElementById("selectBrand").value;					var selModl = document.getElementById("selectModel").value;					var selPric = document.getElementById("selectPrice").value;					var selMile = document.getElementById("selectMileage").value;					var selLocn = document.getElementById("selectLocation").value;					var selBoostName = document.getElementById("selectBoostName").value;										if(selection=='sort') { if(selsrt==selSort) { return false; } else { selsrt = selSort; } }					if(selection=='cond') { if(selcon==selCond) { return false; } else { selcon = selCond; } }					if(selection=='brnd') { if(selbrn==selBrnd) { return false; } else { selbrn = selBrnd; } }					if(selection=='modl') { if(selmod==selModl) { return false; } else { selmod = selModl; } }					if(selection=='pric') { if(selprc==selPric) { return false; } else { selprc = selPric; } }					if(selection=='mile') { if(selmil==selMile) { return false; } else { selmil = selMile; } }					if(selection=='locn') { if(selloc==selLocn) { return false; } else { selloc = selLocn; } }					if(selection=='boostName') { if(selboostName==selBoostName) { return false; } else { selboostName = selBoostName; } }										document.getElementById("mt-buy-list").innerHTML = "Loading....";					window.setTimeout(function(){}, 2000);										$.ajax({						url: 'ajax/buy-car-list.php?sort='+selSort+'&condition='+selCond+'&brand='+selBrnd+'&model='+selModl+'&price='+selPric+'&mileage='+selMile+'&location='+selLocn+'&boostName='+selBoostName,						data: "",						dataType: 'json',						success: function(data)						{							var list = "";							var tk = 0;														for (var i=0; i<=data[8]; i++)							{								tk = 0;								// if(data[2][i]>0) { tk = "<font colo