itsubishi') { arr = mitsubishi; }		else if(sel=='Nissan') { arr = nissan; }		else if(sel=='Porsche') { arr = porsche; }		else if(sel=='Proton') { arr = proton; }		else if(sel=='Subaru') { arr = subaru; }		else if(sel=='Suzuki') { arr = suzuki; }		else if(sel=='Tata') { arr = tata; }		else if(sel=='Toyota') { arr = toyota; }		else if(sel=='Volkswagen') { arr = volkswagen; }		else if(sel=='Volvo') { arr = volvo; }		arr.sort();				var option;		option = document.createElement("option");		option.text = "Any Model";		option.value = "All";		mod.add(option);				for (x of arr) {			option = document.createElement("option");			option.text = x;			mod.add(option);		}	}}</script>										<!--Cars Form-->                                        <div class="cars-form">                                            <form method="post" action="contact.html">                                                <div class="form-group">                                                    <label>Condition:</label>                                                    <select id='selectCondition' class="" onchange="fnListSort('cond')">                                                        <option value='All' selected>New/Used Cars</option>														<option value='New'>New Cars</option>                                                        <option value='Used'>Used Cars</option>                                                    </select>                                                </div>																								<div class="form-group">                                                    <label>Brand:</label>                                                    <select id='selectBrand' class="" onchange="fnBrandModel(this); fnListSort('brnd');">                                                        <option value='All' selected>Any Brand</option><option>Audi</option><option>Bentley</option><option>BMW</option><option>Daihatusu</option><option>Ford</option