<?php session_start();require('files/session.php');if(isset($_POST['submit']) && isset($_POST['name']) && isset($_POST['email']) && isset($_POST['mobile']) && isset($_POST['msg'])){	$name = $_POST['name'];				$name = preg_replace("([^A-Za-z. -])", "", $name);	$email = $_POST['email'];			$email = preg_replace("([^a-z0-9@.-_])", "", strtolower($email));	$mobile = $_POST['mobile'];			$mobile = preg_replace("([^0-9])", "", $mobile);	$msg = $_POST['msg'];				$msg = preg_replace("([^A-Za-z0-9. -,?/;:])", "", $msg);		require('files/ts.php');	require('files/dbcon.php');		$sl = fnRowID("mt_feedback");	$sql = "insert into `$db`.`mt_feedback` values ('$sl', '$name', '$email', '$mobile', '$msg', '$now', NULL, NULL);";	mysqli_query($dbcon, $sql) or die($sql);		/* email */		mysqli_close($dbcon);		die("<script>alert('Thanks for submit your feedback.'); window.location='contact-bn.php';</script>");}?><!DOCTYPE html><html><head><?php require('files/head-bn.php'); ?></head><body><div class="page-wrapper">	<!-- Preloader -->	<div class="preloader"></div>	<?php $menu='contact'; require('files/header-bn.php'); ?>    <!--Page Title-->    <section class="page-title" style="background-image:url(images/background/contact.jpg);" aria-label="Motortrader man holding steering wheel">        <div class="auto-container">            <h1>যোগাযোগ করুন</h1>        </div>    </section>    <!--End Page Title-->    <!--Page Info-->    <section class="page-info">        <div class="auto-container">            <ul class="bread-crumb">                <li><a href="index-bn.php">হোম</a></li>                <li>পেজেস</li>                <li class="current">যোগাযোগ করুন</li>            </ul>        </div>    </section>    <!--End Page Info-->    <!--Contact Form Section-->	<a name='ad'></a>    <section class="contact-form-section">    	<div class="auto-container">        	<div class="sec-title">            	<h2>আমাদের ম্যাসেজ পাঠান</h2>          