<?php session_start();require('files/session.php');if($u!="" && $session==true) {} else { die("<script>window.location='index-bn.php';</script>"); }require('files/ts.php');require('files/dbcon.php');function Ymd2dMY($dt) {				//2014-03-02  >>  02-Mar-2013	$y = substr($dt,0,4);	$m = substr($dt,5,2);	if($m=="01") { $m="Jan"; } elseif($m=="02") { $m="Feb"; } elseif($m=="03") { $m="Mar"; }	elseif($m=="04") { $m="Apr"; } elseif($m=="05") { $m="May"; } elseif($m=="06") { $m="Jun"; }	elseif($m=="07") { $m="Jul"; } elseif($m=="08") { $m="Aug"; } elseif($m=="09") { $m="Sep"; }	elseif($m=="10") { $m="Oct"; } elseif($m=="11") { $m="Nov"; } elseif($m=="12") { $m="Dec"; }	$d = substr($dt,8,2);	$dt = "$d-$m-$y";	return $dt;}?><!DOCTYPE html><html><head><?php require('files/head-bn.php'); ?></head><body><div class="page-wrapper"> 	    <!-- Preloader -->    <div class="preloader"></div> 	    <?php $menu='profile'; require('files/header-bn.php'); ?>		<!--Page Title- ->    <section class="page-title" style="padding:100px 0px; background-image:url(images/background/yellow-bg.jpg);">		<div class="auto-container">            <h1 style='color:#242628;'>আমার প্রোফাইল</h1>        </div>    </section>    <!- -End Page Title-->    <!--Page Info-->    <section class="page-info">        <div class="auto-container">            <ul class="bread-crumb">                <li><a href="index-bn.php">হোম</a></li>                <li>আমার অ্যাকাউন্ট</li>                <li class="current">প্রোফাইল</li>            </ul>        </div>    </section>    <!--End Page Info-->        <!--Register Section-->    <section class="register-section">        <div class="auto-container">            			<div class="sec-title">            	<h2>আমার প্রোফাইল</h2>                <!--<div class="text">Don’t hestiate to ask us something, Our customer support team always ready to help you, <br> they will support you 24/7.</div>-->            </div>						<div class=