a==null) { return false; }					return confirm('Are you sure you want to update your profile info ?');				}				</script>								<form name='frmEdit' method="post" enctype="multipart/form-data" action="profile_edit_submit.php" onsubmit='return validFrmPostProPic()'>								<!--Form Column-->                <div class="form-column column col-lg-3 col-md-3 col-sm-12 col-xs-12">                    <div class="form-title">                        <h2>My Pic</h2>                    </div>                    <div class="styled-form register-form">                        <div class="image">							<?php							if($upic=="" || $upic==null) { echo "<img src='profile_pic/0.png' width=200 height=200>"; }							else { echo "<img src='profile_pic/".$uts."_".$upic."'>"; }							?>						</div>                    </div>					<br>					<input type="file" name="file1" id="file1" accept=".jpg, .JPG, .jpeg, .JPEG, .png, .png12, .png24, .gif, .bmp">                </div>								<!--Form Column-->                <div class="form-column column col-lg-3 col-md-3 col-sm-12 col-xs-12">                    <div class="form-title">                        <h2>My Info</h2>                    </div>                                        <!--Register Form-->                    <div class="styled-form register-form">						<div class="form-group" style='margin-bottom:3px;'>							<span class="adon-icon" style='left:10px;'><span class="fa fa-user"></span></span>							<input type="text" name="name" style='padding-left:30px; border:1px solid #ccc; color:#000;' minlength='3' maxlength=50 value="<?php echo $un; ?>" placeholder="Your Name *" required>						</div>						<div class="form-group" style='margin-bottom:3px;'>							<span class="adon-icon" style='left:10px;'><span class="fa fa-envelope-o"></span></span>							<input type="email" name="email" style='padding-left:30px; border:1px solid #ccc; color:#000;' minlength='3' maxlength=50 value="<?php echo $ue; ?>" placeholder="Email Address *" required>						</div>						<div class="form-group" style='margin-bottom:3px;'>							<span class="adon-icon" style='left:10px;'><span class="fa fa-phone"></span></span>							<input type="text" name="mobile" style='padding-left:30px; border:1px solid #ccc; color:#000;' maxlength=11 value="<?php echo $um; ?>" placeholder="Mobile No." pattern="01[3-9]{1}[0-9]{8}" required>						</div>						<div class="form-group">							<input type='submit' name='submitUpdate' value='UPDATE' class="theme-btn btn-style-one">						</div>                    </div>                </div>								</form>				            </div>        </div>    </section>    <!--End Register Section-->        <?php require('files/footer.php'); ?>    </div><!--End pagewrapper--><!--Scroll to top--><div class="scroll-to-top scroll-to-target" data-target="html"><span class="icon fa fa-angle-up"></span></div><script src="js/jquery.js"></script> <script src="js/bootstrap.min.js"></script><script src="js/jquery.fancybox.pack.js"></script><script src="js/jquery.fancybox-media.js"></script><script src="js/owl.js"></script><script src="js/appear.js"></script><script src="js/jquery-ui.js"></script><script src="js/wow.js"></script><script src="js/script.js"></script></body></html>