<?php session_start();//error_reporting(E_ALL);require('files/session.php');if($u!="" && $session==true) {} else { die("<script>window.location='index.php?lang=En';</script>"); }if(isset($_POST['submitUpdate']) && isset($_POST['name']) && isset($_POST['email']) && isset($_POST['mobile'])){	$name = $_POST['name'];				$name = preg_replace("([^A-Za-z. -])", "", $name);	$email = $_POST['email'];			$email = preg_replace("([^a-z0-9@.-_])", "", strtolower($email));	$mobile = $_POST['mobile'];			$mobile = preg_replace("([^0-9])", "", $mobile);		$allowed = array('jpg', 'JPG', 'jpeg', 'JPEG', 'png', 'png12', 'png24', 'gif', 'bmp');		if($_FILES["file1"]["error"] > 0) { $pp = $filename = ""; }	else {		$filename = strtolower(substr($_FILES['file1']['name'],-15));		$ext = pathinfo($filename, PATHINFO_EXTENSION);		if(!in_array($ext, $allowed)) {			echo "<script>alert('WARNING!!! Image file format not supported.');</script>";			$pp = $filename = "";		}		else		{			$filename = preg_replace("([^A-Za-z0-9.])", "", $filename);						$target = "profile_pic/".$uts."_".$filename;			move_uploaded_file($_FILES["file1"]["tmp_name"], $target) or exit();			$pp = ", profilePic='".$filename."'";		}	}		require_once('files/dbcon.php');	require_once('files/ts.php');		$sql = "UPDATE `$db`.`mt_users_pass` SET name='$name', email='$email', mobile='$mobile'".$pp." where userid='$u' and ts='$uts';";	mysqli_query($dbcon, $sql) or die("$sql");	//echo $sql;		$sl = fnRowID("mt_users_pass_log");	$sql = "insert into `$db`.`mt_users_pass_log` values ('$sl', '$u', '$name', '$email', '$mobile', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '$filename', 'y', '$now', 'EDIT', NULL, NULL, NULL, NULL);";	mysqli_query($dbcon, $sql) or die("$sql");	//echo $sql;		if($pp!="") {		$sl = fnRowID("mt_users_propic_log");		$sql = "insert into `$db`.`mt_users_propic_log` values ('$sl', '$u', '$uts', '$filename', '$now');";		mysqli_query($dbcon, $sql) or die("$sql");		//echo $sql;		$_SESSION['propic'] = $filename;		$_COOKIE['propic'] = $filename;	}		mysqli_close($dbcon);		$_SESSION['username'] = $name;	$_SESSION['email'] = $email;	$_SESSION['mobile'] = $mobile;	//$_SESSION['propic'] = $proPic;	$_COOKIE['username'] = $name;	$_COOKIE['email'] = $email;	$_COOKIE['mobile'] = $mobile;		die("<script>alert('Thanks for update your profile info.'); window.location='profile.php';</script>");		//echo "1";}else{	die("<script>window.location='profile.php';</script>");}?>