email;	$_SESSION['mobile'] = $mobile;	//$_SESSION['propic'] = $proPic;		die("<script>alert('Thanks for update your profile info.'); window.location='profile.php';</script>");}else{	die("<script>window.location='profile.php';</script>");}?>