CompareInput(pid){	var a = document.getElementById('item1');	var b = document.getElementById('item2');		if(a.value==pid || b.value==pid) { return false; }		a.value = b.value;		b.value = pid;		if(a.value=='1') { return false; }	else { document.getElementById('divCompare').style.display = "inline-block"; }		if(document.getElementById('spnComp').innerHTML=="") {		document.getElementById('spnComp').innerHTML = "Compare : 1";		localStorage.setItem("mt_comp1", a.value);		localStorage.setItem("mt_comp2", b.value);	}	else {		document.getElementById('spnComp').innerHTML = "<a href='my-garage-compare.php?pg=buy-car&p1="+a.value+"&p2="+b.value+"' style='background-color:#000; color:#FFF; padding:5px 8px; border-radius:8px;'>Compare</a>";		localStorage.setItem("mt_comp1", a.value);		localStorage.setItem("mt_comp2", b.value);	}}</script><p align=right style='display:none;'><input type='text' id='item1' name='item1' value='1' readonly style='padding:5px; text-align:right;'><input type='text' id='item2' name='item2' value='2' readonly style='padding:5px;'></p><div id='divCompare' style='display:none; position:fixed; bottom:0px; left:0px; padding:10px; background-color:#FFCF00; font-size:14px; font-weight:bold;'>	<span id='spnComp'></span> &nbsp; <a onclick='fnCompareClose()' style='color:#FF0000; font-size:18px;' title='Close'><span class="icon fa fa-times-circle"></span></a></div><script>var cmp1 = localStorage.getItem("mt_comp1");var cmp2 = localStorage.getItem("mt_comp2");document.getElementById('item1').value = cmp1;document.getElementById('item2').value = cmp2;/*if(cmp2>2) {	document.getElementById('divCompare').style.display = 'inline-block';	document.getElementById('spnComp').innerHTML = "<a href='my-garage-compare.php?pg=buy-car&p1="+cmp1+"&p2="+cmp2+"' style='background-color:#000; color:#FFF; padding:5px 8px; border-radius:8px;'>Compare</a>";}else if(cmp1>1) {	document.getElementById('divCompare').style.display = 'inline-block';	document.getElementById('spnComp').innerHTML = 'Compare : 1'+cmp1+'##'+cmp2;}*/if(cmp1!="1" && cmp1!="2" && cmp2!="1" && cmp2!="2") {	document.getElementById('divCompare').style.display = 'inline-block';	document.getElementById('spnComp').innerHTML = "<a href='my-garage-compare.php?pg=buy-car&p1="+cmp1+"&p2="+cmp2+"' style='background-color:#000; color:#FFF; padding:5px 8px; border-radius:8px;'>Compare</a>";}else if(cmp2!="1" && cmp2!="2") {	document.getElementById('divCompare').style.display = 'inline-block';	document.getElementById('spnComp').innerHTML = 'Compare : 1';}</script></body></html>